// FIXED: Collect existing docs that arrive as individual items
const MAX_FILES_PER_RUN = 50;

console.log('=== COMBINE & FILTER (FIXED - Individual Items) ===');

const allInputs = $input.all();
console.log(`üì• Total inputs received: ${allInputs.length}`);

// ============================================
// SEPARATE INPUTS BY TYPE
// ============================================
let existingDocsArray = [];
let meetingsFiles = [];
let strategyFiles = [];
let financialFiles = [];

let meetingsContext = null;
let strategyContext = null;
let financialContext = null;

for (const input of allInputs) {
  const data = input.json;

  // FIX APPLIED HERE:
  // Instead of counting keys (keys.length === 2), we check for properties.
  // Database records have 'source_id' but do NOT have 'mimeType' (which Drive files always have).
  const isExistingDoc = data.source_id !== undefined && 
                        data.source_modified_time !== undefined &&
                        data.mimeType === undefined; 

  if (isExistingDoc) {
    existingDocsArray.push(data);
    continue;
  }

  // TAGGED FILES COLLECTION
  if (data.files && Array.isArray(data.files) && data.folder_type) {
    console.log(`üì¶ Found ${data.folder_type} collection: ${data.files.length} files`);

    switch (data.folder_type) {
      case 'meetings':
        meetingsFiles = data.files;
        meetingsContext = {
          team_id: data.team_id,
          user_id: data.user_id,
          access_token: data.access_token,
          folder_id: data.source_folder
        };
        break;
      case 'strategy':
        strategyFiles = data.files;
        strategyContext = {
          team_id: data.team_id,
          user_id: data.user_id,
          access_token: data.access_token,
          folder_id: data.source_folder
        };
        break;
      case 'financial':
        financialFiles = data.files;
        financialContext = {
          team_id: data.team_id,
          user_id: data.user_id,
          access_token: data.access_token,
          folder_id: data.source_folder
        };
        break;
    }
  }
}

console.log(`\nüìä DATA COLLECTED:`);
console.log(`   Existing docs: ${existingDocsArray.length}`);
console.log(`   Meetings files: ${meetingsFiles.length}`);
console.log(`   Strategy files: ${strategyFiles.length}`);
console.log(`   Financial files: ${financialFiles.length}`);

const currentTeamId = meetingsContext?.team_id || strategyContext?.team_id || financialContext?.team_id;

if (!currentTeamId) {
  console.error('‚ùå CRITICAL: No team_id found');
  return [];
}

console.log(`\nüéØ Processing for team: ${currentTeamId}`);

// ============================================
// BUILD EXISTING DOCS MAP
// ============================================
const existingMap = new Map();

console.log(`\nüìã Building map from ${existingDocsArray.length} existing docs...`);

existingDocsArray.forEach((doc, idx) => {
  if (doc && doc.source_id) {
    const modTime = doc.source_modified_time ? new Date(doc.source_modified_time) : null;
    existingMap.set(doc.source_id, modTime);

    if (idx < 5) {
      console.log(`  ${idx + 1}. ${doc.source_id.substring(0, 40)}... = ${modTime?.toISOString() || 'null'}`);
    }
  }
});

console.log(`‚úÖ Built map with ${existingMap.size} unique source_ids`);

// ============================================
// FILTER FILES
// ============================================
const allNewFiles = [];
let counters = { new: 0, updated: 0, skipped: 0 };

function createFileObject(file, folderType, context, reason, isSheet) {
  return {
    file_id: file.id,
    file_name: file.name,
    mime_type: file.mimeType,
    modified_time: file.modifiedTime,
    web_view_link: file.webViewLink,
    size: file.size,
    folder_type: folderType,
    parent_folder_id: context.folder_id,
    team_id: context.team_id,
    user_id: context.user_id,
    access_token: context.access_token,
    processing_reason: reason,
    is_google_sheet: isSheet
  };
}

function processFile(file, folderType, context) {
  if (!file || !file.id) return;
  if (!context) return;

  if (file.mimeType === 'application/pdf') {
    counters.skipped++;
    return;
  }

  const isGoogleSheet = file.mimeType === 'application/vnd.google-apps.spreadsheet';
  const fileModified = new Date(file.modifiedTime);

  if (isGoogleSheet && folderType === 'financial') {
    let foundExistingTab = false;
    let mostRecentTabModified = null;

    for (const [docSourceId, docModified] of existingMap.entries()) {
      if (docSourceId.startsWith(file.id + '_')) {
        foundExistingTab = true;
        if (docModified && (!mostRecentTabModified || docModified > mostRecentTabModified)) {
          mostRecentTabModified = docModified;
        }
      }
    }

    if (!foundExistingTab) {
      console.log(`   ‚úÖ NEW SHEET: ${file.name}`);
      allNewFiles.push(createFileObject(file, folderType, context, 'new_file', true));
      counters.new++;
      return;
    }

    if (mostRecentTabModified && fileModified > mostRecentTabModified) {
      console.log(`   üîÑ UPDATED SHEET: ${file.name}`);
      allNewFiles.push(createFileObject(file, folderType, context, 'updated_file', true));
      counters.updated++;
      return;
    }

    console.log(`   ‚è≠Ô∏è SKIP: ${file.name}`);
    counters.skipped++;
    return;
  }

  const existingModified = existingMap.get(file.id);

  if (existingModified === undefined) {
    console.log(`   ‚úÖ NEW: ${file.name}`);
    allNewFiles.push(createFileObject(file, folderType, context, 'new_file', false));
    counters.new++;
  } else if (existingModified && fileModified > existingModified) {
    console.log(`   üîÑ UPDATED: ${file.name}`);
    allNewFiles.push(createFileObject(file, folderType, context, 'updated_file', false));
    counters.updated++;
  } else {
    console.log(`   ‚è≠Ô∏è SKIP: ${file.name}`);
    counters.skipped++;
    return;
  }
}

console.log(`\n=== PROCESSING MEETINGS ===`);
if (meetingsContext) {
  meetingsFiles.forEach(f => processFile(f, 'meetings', meetingsContext));
}

console.log(`\n=== PROCESSING STRATEGY ===`);
if (strategyContext) {
  strategyFiles.forEach(f => processFile(f, 'strategy', strategyContext));
}

console.log(`\n=== PROCESSING FINANCIAL ===`);
if (financialContext) {
  financialFiles.forEach(f => processFile(f, 'financial', financialContext));
}

console.log(`\n=== RESULTS ===`);
console.log(`‚úÖ New: ${counters.new}`);
console.log(`üîÑ Updated: ${counters.updated}`);
console.log(`‚è≠Ô∏è Skipped: ${counters.skipped}`);
console.log(`üì§ Total: ${allNewFiles.length}`);

let finalOutput = allNewFiles;
if (allNewFiles.length > MAX_FILES_PER_RUN) {
  console.log(`‚ö†Ô∏è Capping to ${MAX_FILES_PER_RUN} files`);
  finalOutput = allNewFiles.slice(0, MAX_FILES_PER_RUN);
}

if (finalOutput.length === 0) {
  console.log(`‚úÖ Nothing to process`);
  return [{
    json: {
      has_files: false,
      team_id: currentTeamId,
      reason: 'no_processable_files',
      stats: counters
    }
  }];
}

console.log(`üöÄ Outputting ${finalOutput.length} files`);
return finalOutput.map(file => ({ json: { ...file, has_files: true } }));

// Batch new files into groups of 5 - FILTER OUT EMPTY ITEMS
const batchSize = 5;
const allItems = $input.all();

console.log(`=== BATCHING FILES ===`);
console.log(`Total items received: ${allItems.length}`);

// FILTER: Only process items that have files to process
const files = allItems.filter(item => {
  const hasFiles = item.json.has_files === true;
  const isFile = item.json.file_id !== undefined;

  if (!hasFiles && !isFile) {
    console.log(`‚è≠Ô∏è Skipping item: ${JSON.stringify(item.json).substring(0, 100)}...`);
  }

  return hasFiles || isFile;
});

console.log(`Files to process after filtering: ${files.length}`);
console.log(`Batch size: ${batchSize}`);

if (files.length === 0) {
  console.log(`‚ö†Ô∏è No files to process - returning empty result`);
  return [{
    json: {
      has_files: false,
      reason: 'no_files_after_filtering'
    }
  }];
}

const batches = [];
for (let i = 0; i < files.length; i += batchSize) {
  const batch = files.slice(i, i + batchSize);
  batches.push({
    json: {
      batch_number: Math.floor(i / batchSize) + 1,
      total_batches: Math.ceil(files.length / batchSize),
      files: batch.map(item => item.json)
    }
  });
}

console.log(`‚úÖ Created ${batches.length} batches`);
batches.forEach((batch, idx) => {
  console.log(`  Batch ${idx + 1}: ${batch.json.files.length} files`);
});

return batches;

console.log('=== STARTING DOWNLOAD ===');
const items = $input.all();
console.log(`Total input items: ${items.length}`);

const downloadedFiles = [];

for (const item of items) {
  const data = item.json;

  // Handle both batch format and direct file format
  let filesToProcess = [];

  if (data.files && Array.isArray(data.files)) {
    // This is a batch object from "Loop Over Batches"
    filesToProcess = data.files;
    console.log(`DOWNLOADING BATCH ${data.batch_number || '?'}/${data.total_batches || '?'}`);
    console.log(`Files in batch: ${filesToProcess.length}`);
  } else if (data.file_id) {
    // This is a single file object (shouldn't happen, but handle it)
    filesToProcess = [data];
    console.log(`Processing single file: ${data.file_name}`);
  } else {
    // Unknown format - skip
    console.log(`‚ö†Ô∏è Skipping item - no files found:`, Object.keys(data).join(', '));
    continue;
  }

  for (const file of filesToProcess) {
    console.log(`Processing file: ${file.file_name} (${file.file_id})`);

    try {
      const response = await this.helpers.httpRequest({
        method: 'GET',
        url: `https://www.googleapis.com/drive/v3/files/${file.file_id}/export?mimeType=text/plain`,
        headers: {
          'Authorization': `Bearer ${file.access_token}`
        }
      });

      downloadedFiles.push({
        ...file,
        content: response
      });

      console.log(`‚úì Downloaded: ${file.file_name}`);

    } catch (error) {
      console.error(`‚úó Error downloading ${file.file_name}:`, error.message);
      downloadedFiles.push({
        ...file,
        content: '',
        download_error: error.message
      });
    }
  }
}

console.log(`=== TOTAL FILES DOWNLOADED: ${downloadedFiles.length} ===`);
return downloadedFiles.map(file => ({ json: file }));

// Filter out failed downloads
const items = $input.all();
const successfulDownloads = items.filter(item => {
  const file = item.json;

  // Check if content was successfully downloaded
  const hasContent = file.content && file.content.length >= 50;
  const noError = !file.download_error;

  return hasContent && noError;
});

console.log(`Successful downloads: ${successfulDownloads.length}/${items.length}`);

if (successfulDownloads.length === 0) {
  console.log('No successful downloads in this batch');
  return [];
}

return successfulDownloads;

const crypto = require('crypto');
const results = [];

console.log('=== PREPARE DOCUMENTS FOR STORAGE ===');
const inputItems = $input.all();

if (inputItems.length === 0) return [];

// Generate UUID
function generateUUID() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    const r = Math.random() * 16 | 0;
    const v = c == 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

const batchId = generateUUID();

for (const item of $input.all()) {
  const data = item.json;
  const teamId = data.team_id;

  if (!teamId) continue;

  // 1. NORMALIZE CONTENT
  let finalContent = '';
  if (Array.isArray(data.content)) {
    finalContent = data.content.map(segment => segment.sentence || '').join(' ');
  } else if (typeof data.content === 'string') {
    finalContent = data.content;
  } else {
    finalContent = JSON.stringify(data.content || '');
  }
  data.content = finalContent;

  if (!data.content || data.content.length < 50) continue;

  let documentType = 'general_document';
  if (data.folder_type === 'meetings') documentType = 'meetings';
  else if (data.folder_type === 'strategy') documentType = 'strategy';

  const contentHash = crypto.createHash('md5').update(data.content.substring(0, 500)).digest('hex');

  // 2. PREPARE DATES
  // accurately capture the source time, defaulting to now only if missing
  const sourceTime = data.modified_time || data.source_modified_time || new Date().toISOString();
  const processingTime = new Date().toISOString(); // This is the insert time

  const document = {
    team_id: teamId,
    user_id: data.user_id,
    batch_id: batchId,
    source_type: 'google_drive',
    source_id: data.file_id || data.source_id,
    document_type: documentType,
    folder_type: data.folder_type,
    title: (data.file_name || data.title || 'Untitled Document').substring(0, 500),
    file_name: data.file_name,
    content: data.content,
    content_hash: contentHash,
    content_length: data.content.length,
    word_count: data.content.trim().split(/\s+/).length,

    // DB Fields
    processing_timestamp: processingTime, // Insert Date
    source_modified_time: sourceTime,     // Document Date

    metadata: {
      team_id: teamId,
      user_id: data.user_id,
      batch_id: batchId,
      folder_type: data.folder_type,
      file_name: data.file_name,
      // CRITICAL: Put the source time in metadata explicitly
      document_date: sourceTime, 
      source_modified_time: sourceTime,
      processing_timestamp: processingTime
    }
  };

  results.push(document);
}

return results.map(item => ({ json: item }));

const chunks = [];
const chunkSize = 1000;
const overlap = 200;

console.log('=== CHUNK CONTENT NODE ===');
const inputItems = $input.all();

if (inputItems.length === 0) return [];

for (const item of inputItems) {
  const doc = item.json;
  if (!doc || !doc.content || typeof doc.content !== 'string' || doc.content.length < 100) continue;

  const content = doc.content;
  const sentences = content.split(/[.!?]+/).filter(s => s.trim().length > 0);

  let currentChunk = '';
  let chunkIndex = 0;

  // Helper to push chunk
  const addChunk = (text, index) => {
    chunks.push({
      content: text,
      chunk_index: index,
      source_id: doc.source_id,
      source_type: doc.source_type || 'google_drive',
      title: (doc.title || 'Untitled Document').substring(0, 500),
      batch_id: doc.batch_id,
      team_id: doc.team_id,
      user_id: doc.user_id,
      folder_type: doc.folder_type,

      // CRITICAL: Pass the source time through to the chunk
      source_modified_time: doc.source_modified_time, 
      processing_timestamp: doc.processing_timestamp, // Pass insert time separately

      metadata: doc.metadata || {}
    });
  };

  for (const sentence of sentences) {
    const trimmed = sentence.trim();
    if (!trimmed) continue;

    if (currentChunk.length + trimmed.length > chunkSize && currentChunk.length > 0) {
      addChunk(currentChunk.trim(), chunkIndex);

      const words = currentChunk.split(' ');
      const overlapWords = words.slice(-Math.floor(overlap / 5));
      currentChunk = overlapWords.join(' ') + ' ' + trimmed;
      chunkIndex++;
    } else {
      currentChunk += (currentChunk ? '. ' : '') + trimmed;
    }
  }

  if (currentChunk.trim().length > 0) {
    addChunk(currentChunk.trim(), chunkIndex);
  }
}

return chunks.map(chunk => ({ json: chunk }));

const vectorChunks = [];

// 1. Get the Embeddings (Current Input)
const embeddingItems = $input.all();

// 2. Get the Original Chunk Data (From the node BEFORE OpenAI)
// We access the output of "Expand Batch for Processing" to get the content & dates
const originalItems = $("Extract Chunks from Batch").all();

console.log('=== PREPARE VECTOR CHUNKS (MERGED DATA) ===');
console.log(`Embeddings received: ${embeddingItems.length}`);
console.log(`Original chunks found: ${originalItems.length}`);

if (embeddingItems.length === 0) return [];

// Loop through items (assuming 1-to-1 mapping between input and output)
for (let i = 0; i < embeddingItems.length; i++) {

  // Get the Embedding response
  const embeddingJson = embeddingItems[i].json;

  // Get the Original Data (Content, Dates, IDs)
  // We use the same index 'i' because n8n processes these in order
  const originalItem = originalItems[i]?.json;

  // VALIDATION
  if (!originalItem) {
    console.log(`‚ö†Ô∏è Error: No original item found for index ${i}`);
    continue;
  }

  // Get Content from Original, Embedding from Input
  const content = originalItem.content;
  const embeddingArray = embeddingJson.data?.[0]?.embedding;

  if (!content || !embeddingArray) {
    console.log(`‚ö†Ô∏è Skipping item ${i}: Missing content or embedding`);
    // Debug logging
    if (!content) console.log(`   - Content missing`);
    if (!embeddingArray) console.log(`   - Embedding missing (Check OpenAI Quota/Key)`);
    continue;
  }

  // 1. Extract Dates (From Original Item)
  // PRIORITY: source_modified_time (passed from chunker) > metadata > NOW
  let documentDate = originalItem.source_modified_time || 
                     originalItem.metadata?.document_date || 
                     originalItem.metadata?.modified_time || 
                     new Date().toISOString();

  // Ensure format is ISO
  try {
     documentDate = new Date(documentDate).toISOString();
  } catch (e) {
     documentDate = new Date().toISOString();
  }

  // Insert Time (From Original Item)
  const processingTimestamp = originalItem.processing_timestamp || new Date().toISOString();

  const vectorString = `[${embeddingArray.join(',')}]`;

  // 2. Prepare Metadata for DB
  const metadataForDB = {
    document_name: (originalItem.title || '').substring(0, 500),
    chunk_index: originalItem.chunk_index || 0,
    document_type: originalItem.folder_type === 'meetings' ? 'Meetings' : 'Strategy',
    source_id: originalItem.source_id,

    // DATES IN METADATA
    document_date: documentDate, // <--- File Date
    processing_timestamp: processingTimestamp, // <--- Insert Date

    batch_id: originalItem.batch_id,
    team_id: originalItem.team_id,
    ...originalItem.metadata // Spread original metadata
  };

  // Explicitly ensure date is correct in metadata
  metadataForDB.document_date = documentDate;

  vectorChunks.push({
    content: content,
    title: (originalItem.title || '').substring(0, 500),

    // SQL Column: document_date (Set to File Date)
    document_date: documentDate, 

    batch_id: originalItem.batch_id,
    team_id: originalItem.team_id,
    source_id: originalItem.source_id,
    folder_type: originalItem.folder_type,

    // Metadata JSONB
    metadata: JSON.stringify(metadataForDB),

    embedding: vectorString
  });

  console.log(`‚úì Prepared Chunk ${i} for "${metadataForDB.document_name}"`);
}

return vectorChunks.map(chunk => ({ json: chunk }));

// Enhanced metrics to account for batch processing
console.log('=== ALL CHUNK BATCHES COMPLETE ===');

// Get total counts across all batches
const loopNode = $('Loop Over Chunk Batches');
const totalBatches = loopNode.first().json.total_batches;

console.log(`Processed ${totalBatches} batches of chunks`);

// Get final storage counts
let totalMeetingsStored = 0;
let totalStrategyStored = 0;

try {
  const allMeetings = $('Store Vector Chunks - Meetings').all();
  totalMeetingsStored = allMeetings.length;
} catch (error) {
  console.log('No meetings vectors stored');
}

try {
  const allStrategy = $('Store Vector Chunks - Strategy').all();
  totalStrategyStored = allStrategy.length;
} catch (error) {
  console.log('No strategy vectors stored');
}

const totalVectorsStored = totalMeetingsStored + totalStrategyStored;

const batchSummary = {
  batch_completed: true,
  total_batches_processed: totalBatches,
  total_vectors_stored: totalVectorsStored,
  meetings_chunks: totalMeetingsStored,
  strategy_chunks: totalStrategyStored,
  timestamp: new Date().toISOString()
};

console.log(`Total vectors stored: ${totalVectorsStored}`);
console.log(`  - Meetings: ${totalMeetingsStored}`);
console.log(`  - Strategy: ${totalStrategyStored}`);

// ‚ö†Ô∏è IMPORTANT: No longer references document storage
// That happens in a separate path after this node

return [{ json: batchSummary }];

// Final execution summary - with safe node access
const teamConnection = $('Loop Over Each Team').item.json;

let batchesProcessed = 0;
try {
  const allBatches = $('Loop Over Batches').all();
  batchesProcessed = allBatches ? allBatches.length : 0;
} catch (error) {
  console.log('No batches were processed (no new files)');
}

const executionSummary = {
  workflow_name: 'Astra Multi-Team Data Sync v3 (Pre-Filtered)',
  workflow_version: 'v3',
  execution_id: $execution.id,
  completion_time: new Date().toISOString(),
  status: 'completed_successfully',

  team_info: {
    team_id: teamConnection.team_id,
    batches_processed: batchesProcessed,
    had_new_files: batchesProcessed > 0
  },

  optimization_features: [
    'Pre-filtering before download',
    'Improved null handling for source_modified_time',
    'Explicit three-case logic (new/updated/skip)',
    'Early exit when no files to process',
    'Better logging and diagnostics',
    'Safe node access with try-catch'
  ]
};

console.log('Team processing completed!');
console.log(JSON.stringify(executionSummary, null, 2));

return [{ json: executionSummary }];

const response = $input.first().json;
// Safe context retrieval inside the branch
const teamData = $('Loop Over Each Team').item.json; 

if (!response || !response.files) {
  console.log('‚ö†Ô∏è No meetings files found');
  // Even if empty, return context so downstream nodes know who this is
  return [{
    json: {
      files: [],
      folder_type: 'meetings',
      team_id: teamData.team_id,
      user_id: teamData.user_id,
      access_token: teamData.access_token,
      parent_folder_id: teamData.meetings_folder_id
    }
  }];
}

console.log(`Tagging ${response.files.length} files as 'meetings' for team ${teamData.team_id}`);

return [{
  json: {
    ...response,
    folder_type: 'meetings',
    source_folder: 'meetings',
    // Pass context forward
    team_id: teamData.team_id,
    user_id: teamData.user_id,
    access_token: teamData.access_token,
    parent_folder_id: teamData.meetings_folder_id
  }
}];

const allItems = $input.all();

console.log('=== FILTER STRATEGY ONLY ===');
console.log(`Input items: ${allItems.length}`);

const strategyItems = allItems.filter(item => {
  const metadata = item.json?.metadata;

  // Parse metadata if it's a string
  let meta = metadata;
  if (typeof metadata === 'string') {
    try {
      meta = JSON.parse(metadata);
    } catch (e) {
      console.log('Error parsing metadata:', e.message);
      return false;
    }
  }

  const docType = meta?.document_type;
  const folderType = meta?.folder_type || item.json?.folder_type;

  console.log(`Item: "${item.json?.title?.substring(0, 30)}" - type: ${docType}, folder: ${folderType}`);

  // Match on either document_type or folder_type
  return docType === 'Strategy' || folderType === 'strategy';
});

console.log(`Found ${strategyItems.length} strategy items`);

if (strategyItems.length === 0) {
  return [];
}

return strategyItems;

const allItems = $input.all();

console.log('=== FILTER MEETINGS ONLY ===');
console.log(`Input items: ${allItems.length}`);

const meetingsItems = allItems.filter(item => {
  const metadata = item.json?.metadata;

  // Parse metadata if it's a string
  let meta = metadata;
  if (typeof metadata === 'string') {
    try {
      meta = JSON.parse(metadata);
    } catch (e) {
      console.log('Error parsing metadata:', e.message);
      return false;
    }
  }

  const docType = meta?.document_type;
  const folderType = meta?.folder_type || item.json?.folder_type;

  console.log(`Item: "${item.json?.title?.substring(0, 30)}" - type: ${docType}, folder: ${folderType}`);

  // Match on either document_type or folder_type
  return docType === 'Meetings' || folderType === 'meetings';
});

console.log(`Found ${meetingsItems.length} meetings items`);

if (meetingsItems.length === 0) {
  return [];
}

return meetingsItems;

const response = $input.first().json;
const teamData = $('Loop Over Each Team').item.json;

if (!response || !response.files) {
  console.log('‚ö†Ô∏è No financial files found');
  return [{
    json: {
