    const data = item.json;
    if (data.sheetName) {
      // Normalize: remove quotes, trim
      const cleanName = data.sheetName.replace(/^'|'$/g, '').trim();
      metaMap.set(cleanName, data);
      // Also map by original name just in case
      metaMap.set(data.sheetName, data);
    }
    indexMap.set(index, data);
  });
  console.log(`Built metadata map with ${metaMap.size} keys`);
}

// 4. Helper to extract sheet name from range string
function getSheetNameFromRange(rangeStr) {
  if (!rangeStr) return null;
  // Range format: "'Sheet Name'!A1:Z" or "SheetName!A1:Z"
  const parts = rangeStr.split('!');
  if (parts.length < 2) return null;

  // Get the part before '!'
  let sheetPart = parts[0];

  // Remove surrounding single quotes if present
  if (sheetPart.startsWith("'") && sheetPart.endsWith("'")) {
    sheetPart = sheetPart.substring(1, sheetPart.length - 1);
  }

  return sheetPart;
}

// 5. PROCESS ITEMS
for (let i = 0; i < inputItems.length; i++) {
  const apiResponse = inputItems[i].json;

  // A. Try to find Metadata
  let tabMetadata = null;
  let matchMethod = 'none';

  // Method 1: Match by Sheet Name (Most Robust)
  if (apiResponse.range) {
    const rangeSheetName = getSheetNameFromRange(apiResponse.range);
    if (rangeSheetName) {
      tabMetadata = metaMap.get(rangeSheetName);
      if (tabMetadata) matchMethod = 'name_match';
    }
  }

  // Method 2: Fallback to Index (Only if name match failed)
  if (!tabMetadata && indexMap.has(i)) {
    tabMetadata = indexMap.get(i);
    if (tabMetadata) {
        console.log(`‚ö†Ô∏è Falling back to index match for item ${i}`);
        matchMethod = 'index_fallback';
    }
  }

  // Validation
  if (!tabMetadata) {
    console.log(`‚ùå Skipping item ${i}: Could not find metadata. Range: "${apiResponse.range || 'unknown'}"`);
    continue;
  }

  // Check for empty data
  if (!apiResponse.values || !Array.isArray(apiResponse.values) || apiResponse.values.length === 0) {
    console.log(`‚ö†Ô∏è Skipping empty tab: ${tabMetadata.sheetName}`);
    continue;
  }

  console.log(`‚úì Matched "${tabMetadata.sheetName}" (Method: ${matchMethod})`);

  // B. Process the Content (CSV Generation)
  const rows = apiResponse.values;
  const headers = rows[0] || [];
  // Filter out completely empty rows
  const dataRows = rows.slice(1).filter(row => row.some(cell => cell && String(cell).trim() !== ''));

  if (dataRows.length === 0) {
    console.log(`  Skipping: Header only or empty content`);
    continue;
  }

  // Create CSV Content
  const csvContent = [
    // Headers
    (Array.isArray(rows[0]) ? rows[0] : [rows[0]])
    .map(h => `"${String(h || '').replace(/"/g, '""')}"`)
    .join(','),
    // Rows
    ...rows.slice(1).map(row => {
      const rowArray = Array.isArray(row) ? row : [row];
      return rowArray
        .map(cell => `"${String(cell || '').replace(/"/g, '""')}"`)
        .join(',');
    })
  ].join('\n');

  // C. Classification Logic
  const classification = classifyFinancialDocument(
    tabMetadata.spreadsheetName, 
