    content: csvContent.substring(0, 150000), // Limit size safe for Postgres

    // CRITICAL: Pass original file date
    source_modified_time: sourceTime,

    // Pass through Team/User Context
    team_id: tabMetadata.team_id,
    user_id: tabMetadata.user_id,

    // Store Tab Info
    tab_metadata: {
      spreadsheet_id: tabMetadata.spreadsheetId,
      spreadsheet_name: tabMetadata.spreadsheetName,
      sheet_name: tabMetadata.sheetName,
      sheet_id: tabMetadata.sheetId,
