// ================= HELPER FUNCTIONS =================

function analyzeDocumentTitle(spreadsheetName, sheetName) {
  const fullTitle = `${spreadsheetName} ${sheetName}`.toLowerCase();
  const classification = {
    document_type: 'other',
    confidence: 0,
    tags: [],
    entity: null
  };

  // Entity detection
  const entityMatch = fullTitle.match(/\b([A-Z]{2,4})\b/i) || fullTitle.match(/\b(llc|inc|corp)\b/i);
  if (entityMatch) classification.entity = entityMatch[1].toUpperCase();

  // Equity
  if (fullTitle.includes('equity') || fullTitle.includes('cap table') || fullTitle.includes('ownership')) {
    return { ...classification, document_type: 'equity', confidence: 0.9, tags: ['equity', 'cap-table'] };
  }
  // P&L
  if ((fullTitle.includes('profit') && fullTitle.includes('loss')) || fullTitle.includes('p&l') || fullTitle.includes('income')) {
    return { ...classification, document_type: 'income_statement', confidence: 0.9, tags: ['p&l', 'income-statement'] };
  }
  // Balance Sheet
  if (fullTitle.includes('balance') && fullTitle.includes('sheet')) {
    return { ...classification, document_type: 'balance_sheet', confidence: 0.9, tags: ['balance-sheet'] };
  }
  // Transactions
  if (fullTitle.includes('transaction') || fullTitle.includes('ledger') || fullTitle.includes('register') || fullTitle.includes('journal')) {
    return { ...classification, document_type: 'transactions', confidence: 0.8, tags: ['transactions'] };
  }

  return classification;
}

function classifyFinancialDocument(spreadsheetName, sheetName, content, tabIndex, totalTabs) {
  const titleAnalysis = analyzeDocumentTitle(spreadsheetName, sheetName);
  const contentLower = content.toLowerCase().substring(0, 2000); 

  let docType = titleAnalysis.document_type;
  let confidence = titleAnalysis.confidence;
  let category = 'general_financial';
  const tags = new Set(titleAnalysis.tags || []);

  // Content-based Refinement
  if (docType === 'other') {
    if (contentLower.includes('asset') && contentLower.includes('liability') && contentLower.includes('equity')) {
      docType = 'balance_sheet';
      category = 'balance_sheet_items';
      confidence = 0.8;
    } else if ((contentLower.includes('revenue') || contentLower.includes('sales')) && contentLower.includes('expense')) {
      docType = 'income_statement';
      category = 'income_statement_items';
      confidence = 0.8;
    } else if (contentLower.includes('date') && contentLower.includes('description') && (contentLower.includes('amount') || contentLower.includes('debit'))) {
      docType = 'transactions';
      category = 'transactions';
      confidence = 0.7;
    }
  }

  // Period Extraction
  let period = null;
  let startDate = null;
  let endDate = null;

  // Simple month regex
  const months = ['jan', 'feb', 'mar', 'apr', 'may', 'jun', 'jul', 'aug', 'sep', 'oct', 'nov', 'dec'];
  for (let i=0; i<months.length; i++) {
    if (sheetName.toLowerCase().includes(months[i])) {
       const yearMatch = sheetName.match(/20\d\d/) || spreadsheetName.match(/20\d\d/);
       const year = yearMatch ? yearMatch[0] : new Date().getFullYear();
       const monthNum = (i+1).toString().padStart(2, '0');
       period = `${year}-${monthNum}`;
       startDate = `${period}-01`;
       // Simple end date approximation
       endDate = `${period}-28`; 
       break;
    }
  }

  if (period) tags.add(`period-${period}`);
  tags.add(docType);
  if (totalTabs > 1) tags.add('multi-tab');

  return {
    document_type: docType,
    confidence: confidence,
    data_category: category,
    keywords: Array.from(tags),
    tags: Array.from(tags),
    period_info: {
      financial_period: period,
      period_start_date: startDate,
      period_end_date: endDate,
      period_type: period ? 'monthly' : null
    },
    confidence: {
        level: confidence > 0.8 ? 'high' : 'medium',
        score: confidence
    }
  };
}

