function chunkBankStatements(doc) { const content = doc.content; const lines = content.split('\n').filter(line => line.trim().length > 0); if (lines.length <= 1) return [content]; const header = lines[0]; const dataLines = lines.slice(1); const linesPerChunk = 50; const chunks = []; for (let i = 0; i < dataLines.length; i += linesPerChunk) { const chunkLines = dataLines.slice(i, i + linesPerChunk); chunks.push([header, ...chunkLines].join('\n')); } return chunks.length > 0 ? chunks : [content]; }
function chunkFinancialStatements(doc) { const content = doc.content; const lines = content.split('\n'); if (lines.length <= 1) return [content]; const header = lines[0]; const dataLines = lines.slice(1); const linesPerChunk = 35; const chunks = []; for (let i = 0; i < dataLines.length; i += linesPerChunk) { const chunkLines = dataLines.slice(i, i + linesPerChunk); chunks.push([header, ...chunkLines].join('\n')); } return chunks.length > 0 ? chunks : [content]; }
function chunkGenericFinancialContent(doc) { const content = doc.content; const lines = content.split('\n'); const chunks = []; let currentChunk = ''; lines.forEach(line => { if (currentChunk.length + line.length > chunkSize) { if (currentChunk.trim().length > 0) { chunks.push(currentChunk.trim()); } currentChunk = line; } else { currentChunk += '\n' + line; } }); if (currentChunk.trim().length > 0) { chunks.push(currentChunk.trim()); } return chunks.length > 0 ? chunks : [content]; }
function determineChunkType(content, docType, chunkIndex, totalChunks) { if (docType === 'transactions' || docType === 'bank_statement') { return chunkIndex === 0 ? 'table_header' : 'row'; } else if (docType === 'income_statement' || docType === 'balance_sheet') { if (chunkIndex === 0) return 'table_header'; if (chunkIndex === totalChunks - 1) return 'summary'; return 'sheet_section'; } return 'sheet_section'; }
function getChunkingStrategy(docType) { switch (docType) { case 'transactions': case 'bank_statement': return 'transaction_based'; case 'balance_sheet': case 'income_statement': return 'section_based'; case 'cash_flow': return 'flow_based'; default: return 'content_based'; } }

